export function isValidMove(board, from, to, turn) {
    const piece = board[from.row][from.col];
    if (!piece) return false;  // No piece to move

    const isWhite = piece === piece.toUpperCase();
    const isBlack = !isWhite;

    // Ensure the correct player is moving
    if ((turn === "white" && !isWhite) || (turn === "black" && !isBlack)) {
      return false;
    }

    const targetPiece = board[to.row][to.col];
    const isCapture = targetPiece !== "";  // If there's an opponent piece

    // Prevent capturing own pieces
    if (isCapture) {
      const targetIsWhite = targetPiece === targetPiece.toUpperCase();
      if ((isWhite && targetIsWhite) || (isBlack && !targetIsWhite)) {
        return false;
      }
    }

    // Pawn movement logic
    if (piece.toLowerCase() === "p") {
      const direction = isWhite ? -1 : 1;
      const startRow = isWhite ? 6 : 1;

      // Move forward one square
      if (to.row === from.row + direction && to.col === from.col && !targetPiece) {
        return true;
      }

      // Move two squares from starting position
      if (from.row === startRow && to.row === from.row + 2 * direction && to.col === from.col && !targetPiece) {
        return true;
      }

      // Capture diagonally
      if (to.row === from.row + direction && Math.abs(to.col - from.col) === 1 && isCapture) {
        return true;
      }
    }

    // Rook movement (straight lines only)
    if (piece.toLowerCase() === "r") {
      if (from.row === to.row) {
        for (let col = Math.min(from.col, to.col) + 1; col < Math.max(from.col, to.col); col++) {
          if (board[from.row][col]) return false;
        }
        return true;
      }
      if (from.col === to.col) {
        for (let row = Math.min(from.row, to.row) + 1; row < Math.max(from.row, to.row); row++) {
          if (board[row][from.col]) return false;
        }
        return true;
      }
    }

    // Knight movement (L-shape)
    if (piece.toLowerCase() === "n") {
      const rowDiff = Math.abs(to.row - from.row);
      const colDiff = Math.abs(to.col - from.col);
      if ((rowDiff === 2 && colDiff === 1) || (rowDiff === 1 && colDiff === 2)) {
        return true;
      }
    }

    // Bishop movement (diagonal only)
    if (piece.toLowerCase() === "b") {
      const rowDiff = Math.abs(to.row - from.row);
      const colDiff = Math.abs(to.col - from.col);
      if (rowDiff === colDiff) {
        let rowStep = to.row > from.row ? 1 : -1;
        let colStep = to.col > from.col ? 1 : -1;
        let row = from.row + rowStep;
        let col = from.col + colStep;
        while (row !== to.row && col !== to.col) {
          if (board[row][col]) return false;
          row += rowStep;
          col += colStep;
        }
        return true;
      }
    }

    // Queen movement (combines rook and bishop)
    if (piece.toLowerCase() === "q") {
      // Check if moving like a rook (horizontal/vertical)
      if (from.row === to.row || from.col === to.col) {
        return isValidMove(board, from, to, turn);  // Rook logic
      }

      // Check if moving like a bishop (diagonal)
      const rowDiff = Math.abs(to.row - from.row);
      const colDiff = Math.abs(to.col - from.col);
      if (rowDiff === colDiff) {
        return isValidMove(board, from, to, turn);  // Bishop logic
      }

      return false;
    }

    // King movement (one square in any direction)
    if (piece.toLowerCase() === "k") {
      const rowDiff = Math.abs(to.row - from.row);
      const colDiff = Math.abs(to.col - from.col);
      if (rowDiff <= 1 && colDiff <= 1) {
        return true;
      }
    }

    return false;  // Invalid move
  }
