import React, { useState } from 'react';
import { initialBoard, pieceSymbols } from '../utils/chess';
import './ChessBoard.css';

const ChessBoard = () => {
  const [board, setBoard] = useState(initialBoard);
  const [selectedCell, setSelectedCell] = useState(null);

  const handleCellClick = (row, col) => {
    // If no cell is selected, select one if there is a piece
    if (!selectedCell && board[row][col] !== '') {
      setSelectedCell({ row, col });
      return;
    }
    // Otherwise, move the piece from the selected cell to the clicked cell
    if (selectedCell) {
      const newBoard = board.map((rowData) => [...rowData]); // deep copy
      newBoard[row][col] = board[selectedCell.row][selectedCell.col];
      newBoard[selectedCell.row][selectedCell.col] = '';
      setBoard(newBoard);
      setSelectedCell(null);
    }
  };

  return (
    <div className="chess-board">
      {board.map((rowData, rowIndex) =>
        rowData.map((cellData, colIndex) => {
          const isLight = (rowIndex + colIndex) % 2 === 0;
          const isSelected =
            selectedCell &&
            selectedCell.row === rowIndex &&
            selectedCell.col === colIndex;
          return (
            <div
              key={`${rowIndex}-${colIndex}`}
              className={`cell ${isLight ? 'light' : 'dark'} ${isSelected ? 'selected' : ''}`}
              onClick={() => handleCellClick(rowIndex, colIndex)}
            >
              {cellData && pieceSymbols[cellData]}
            </div>
          );
        })
      )}
    </div>
  );
};

export default ChessBoard;
